import React, { useEffect, useState } from 'react';
import { AdminAPI, setApiErrorHandler, formatDateTime } from './api';

async function createStaffUser({ username, pin, name, role }) {
  try {
    const res = await fetch('/api/admin/staff', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, pin, name, role }),
    });
    if (!res.ok) {
      const text = await res.text();
      throw new Error(text || `HTTP ${res.status}`);
    }
    return res.json();
  } catch (e) {
    throw e;
  }
}

function App() {
  const [status, setStatus] = useState('');
  const [apiDown, setApiDown] = useState(false);
  const [tab, setTab] = useState('staff'); // 'staff' | 'menu' | 'till'

  useEffect(() => {
    setApiErrorHandler((err, info) => {
      const status = info && typeof info.status === 'number' ? info.status : null;
      setApiDown(!!err && (status == null || status >= 500));
    });
  }, []);
  const defaults = [
    { username: 'HStaff1', pin: 'Zwinger2026!', name: 'Hotel Staff 1', role: 'staff' },
    { username: 'MStaff1', pin: 'Zanger2026!', name: 'Manager Staff 1', role: 'staff' },
  ];

  async function seedDefaults() {
    setStatus('');
    const results = [];
    for (const d of defaults) {
      try {
        const created = await createStaffUser(d);
        results.push(`${d.username}: OK (${created._id || 'created'})`);
      } catch (e) {
        results.push(`${d.username}: ${e.message}`);
      }
    }
    setStatus(results.join(' | '));
  }
  return (
    <div style={{ fontFamily: 'Inter, Arial, sans-serif', background: '#f7f7fa', minHeight: '100vh' }}>
      <header style={{ background: '#1a2236', color: '#fff', padding: '1.5rem 2rem', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <span style={{ fontWeight: 700, fontSize: 28 }}>Floki's Hotel - Admin</span>
        <nav style={{ display: 'flex', gap: '0.5rem' }}>
          <button onClick={() => setTab('staff')} style={{ padding: '8px 12px', borderRadius: 8, border: 'none', background: tab === 'staff' ? '#ffb300' : '#2d3a58', color: '#fff', fontWeight: 700 }}>Staff</button>
          <button onClick={() => setTab('menu')} style={{ padding: '8px 12px', borderRadius: 8, border: 'none', background: tab === 'menu' ? '#ffb300' : '#2d3a58', color: '#fff', fontWeight: 700 }}>Menu</button>
          <button onClick={() => setTab('till')} style={{ padding: '8px 12px', borderRadius: 8, border: 'none', background: tab === 'till' ? '#ffb300' : '#2d3a58', color: '#fff', fontWeight: 700 }}>Till</button>
        </nav>
      </header>
      {apiDown && (
        <div style={{ background: '#fff4e5', color: '#8a5a00', borderBottom: '1px solid #ffd8a8', padding: '8px 12px', textAlign: 'center', fontSize: 14 }}>
          Backend unreachable. Please ensure the server is running on port 5000.
        </div>
      )}
      <main style={{ maxWidth: 900, margin: '2rem auto', background: '#fff', borderRadius: 12, boxShadow: '0 2px 16px #0001', padding: '2rem' }}>
        {tab === 'staff' && (
          <StaffPanel />
        )}
        {tab === 'menu' && (
          <div>
            <h2 style={{ color: '#1a2236' }}>Menu Management</h2>
            <p style={{ color: '#6a708a' }}>Use hotel app for detailed item editing; this section can be expanded later.</p>
          </div>
        )}
        {tab === 'till' && (
          <TillPanel />
        )}
        <hr style={{ margin: '1.5rem 0', border: 'none', borderTop: '1px solid #eee' }} />
        <h3 style={{ color: '#1a2236' }}>Seed Hotel Staff Credentials</h3>
        <p style={{ color: '#6a708a' }}>Creates the following staff users in the backend:</p>
        <ul>
          <li>HStaff1 / Zwinger2026!</li>
          <li>MStaff1 / Zanger2026!</li>
        </ul>
        <button onClick={seedDefaults} style={{ background: '#1a2236', color: '#fff', fontWeight: 700, border: 'none', borderRadius: 8, padding: '0.5rem 0.75rem', cursor: 'pointer' }}>
          Create Default Staff Accounts
        </button>
        {status && (
          <div style={{ marginTop: 12, color: '#1a2236' }}>{status}</div>
        )}
      </main>
      <footer style={{ textAlign: 'center', color: '#888', padding: 24, fontSize: 15 }}>
        &copy; {new Date().getFullYear()} Floki's Hotel. All rights reserved.
      </footer>
    </div>
  );
}

export default App;

function StaffPanel() {
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [form, setForm] = useState({ username: '', name: '', role: 'staff', pin: '' });
  const [submitting, setSubmitting] = useState(false);

  async function load() {
    setLoading(true);
    setError('');
    try {
      const data = await AdminAPI.listStaff();
      setList(data);
    } catch (e) {
      setError(e.message || 'Failed to load staff');
    } finally {
      setLoading(false);
    }
  }
  useEffect(() => { load(); }, []);

  function update(k, v) { setForm(prev => ({ ...prev, [k]: v })); }

  async function createStaff(e) {
    e.preventDefault();
    setSubmitting(true);
    setError('');
    try {
      const created = await AdminAPI.createStaff(form);
      setList(prev => [created, ...prev]);
      setForm({ username: '', name: '', role: 'staff', pin: '' });
    } catch (e) {
      setError(e.message || 'Failed to create staff');
    } finally {
      setSubmitting(false);
    }
  }

  async function toggleActive(item) {
    const updated = await AdminAPI.updateStaff(item._id, { active: !item.active });
    setList(prev => prev.map(s => s._id === updated._id ? updated : s));
  }

  async function resetPin(item) {
    const pin = window.prompt(`Enter new PIN/password for ${item.username}`);
    if (!pin) return;
    const updated = await AdminAPI.updateStaff(item._id, { pin });
    setList(prev => prev.map(s => s._id === updated._id ? updated : s));
  }

  async function deleteStaff(item) {
    if (!window.confirm(`Delete ${item.username}?`)) return;
    await AdminAPI.deleteStaff(item._id);
    setList(prev => prev.filter(s => s._id !== item._id));
  }

  return (
    <div>
      <h2 style={{ color: '#1a2236' }}>Staff Management</h2>
      {error && <div style={{ color: 'crimson', marginBottom: 8 }}>{error}</div>}
      <form onSubmit={createStaff} style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: 12, marginBottom: 16 }}>
        <div>
          <label style={{ display: 'block', fontSize: 12, color: '#6a708a' }}>Username</label>
          <input value={form.username} onChange={e => update('username', e.target.value)} required style={{ width: '100%', padding: 10, borderRadius: 8, border: '1px solid #e1e4ee' }} />
        </div>
        <div>
          <label style={{ display: 'block', fontSize: 12, color: '#6a708a' }}>Name</label>
          <input value={form.name} onChange={e => update('name', e.target.value)} style={{ width: '100%', padding: 10, borderRadius: 8, border: '1px solid #e1e4ee' }} />
        </div>
        <div>
          <label style={{ display: 'block', fontSize: 12, color: '#6a708a' }}>Role</label>
          <select value={form.role} onChange={e => update('role', e.target.value)} style={{ width: '100%', padding: 10, borderRadius: 8, border: '1px solid #e1e4ee' }}>
            <option value="staff">Staff</option>
            <option value="manager">Manager</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <div>
          <label style={{ display: 'block', fontSize: 12, color: '#6a708a' }}>PIN / Password</label>
          <input type="password" value={form.pin} onChange={e => update('pin', e.target.value)} required style={{ width: '100%', padding: 10, borderRadius: 8, border: '1px solid #e1e4ee' }} />
        </div>
        <div style={{ alignSelf: 'end' }}>
          <button type="submit" disabled={submitting} style={{ padding: '10px 14px', borderRadius: 8, border: 'none', background: '#1a2236', color: '#fff', fontWeight: 700 }}>
            {submitting ? 'Creating…' : 'Create Staff'}
          </button>
        </div>
      </form>

      {loading ? (
        <div>Loading…</div>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ textAlign: 'left', borderBottom: '1px solid #eee' }}>
              <th>Username</th>
              <th>Name</th>
              <th>Role</th>
              <th>Active</th>
              <th>Created</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {list.map(item => (
              <tr key={item._id} style={{ borderBottom: '1px solid #f3f4f8' }}>
                <td>{item.username}</td>
                <td>{item.name || '-'}</td>
                <td>{item.role || 'staff'}</td>
                <td>
                  <span style={{ padding: '2px 8px', borderRadius: 999, background: item.active ? '#ddf6e5' : '#ffe9e6', color: item.active ? '#1c7d42' : '#b23b2e', fontSize: 12 }}>
                    {item.active ? 'Yes' : 'No'}
                  </span>
                </td>
                <td>{formatDateTime(item.createdAt)}</td>
                <td style={{ display: 'flex', gap: 8 }}>
                  <button onClick={() => toggleActive(item)} style={{ padding: '6px 10px', borderRadius: 6, border: 'none', background: '#2d3a58', color: '#fff' }}>{item.active ? 'Deactivate' : 'Activate'}</button>
                  <button onClick={() => resetPin(item)} style={{ padding: '6px 10px', borderRadius: 6, border: 'none', background: '#ffb300', color: '#1a2236' }}>Reset PIN</button>
                  <button onClick={() => deleteStaff(item)} style={{ padding: '6px 10px', borderRadius: 6, border: 'none', background: '#b23b2e', color: '#fff' }}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

function TillPanel() {
  const [data, setData] = useState({ tillNumber: '', businessName: '' });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => { load(); }, []);
  async function load() {
    setLoading(true);
    setError('');
    try {
      const t = await AdminAPI.getTill();
      setData({ tillNumber: t?.tillNumber || '', businessName: t?.businessName || '' });
    } catch (e) {
      setError(e.message || 'Failed to load till');
    } finally {
      setLoading(false);
    }
  }

  async function save() {
    setSaving(true);
    setError('');
    try {
      await AdminAPI.updateTill(data);
    } catch (e) {
      setError(e.message || 'Failed to save till');
    } finally {
      setSaving(false);
    }
  }

  return (
    <div>
      <h2 style={{ color: '#1a2236' }}>Till Settings</h2>
      {error && <div style={{ color: 'crimson', marginBottom: 8 }}>{error}</div>}
      {loading ? (
        <div>Loading…</div>
      ) : (
        <div style={{ display: 'grid', gap: 12, maxWidth: 520 }}>
          <div>
            <label style={{ display: 'block', fontSize: 12, color: '#6a708a' }}>Business Name</label>
            <input value={data.businessName} onChange={e => setData({ ...data, businessName: e.target.value })} style={{ width: '100%', padding: 10, borderRadius: 8, border: '1px solid #e1e4ee' }} />
          </div>
          <div>
            <label style={{ display: 'block', fontSize: 12, color: '#6a708a' }}>Till Number</label>
            <input value={data.tillNumber} onChange={e => setData({ ...data, tillNumber: e.target.value })} style={{ width: '100%', padding: 10, borderRadius: 8, border: '1px solid #e1e4ee' }} />
          </div>
          <div style={{ display: 'flex', gap: 8 }}>
            <button onClick={save} disabled={saving} style={{ padding: '10px 14px', borderRadius: 8, border: 'none', background: '#1a2236', color: '#fff', fontWeight: 700 }}>{saving ? 'Saving…' : 'Save'}</button>
            <button onClick={load} style={{ padding: '10px 14px', borderRadius: 8, border: '1px solid #dfe3ee', background: '#fff' }}>Refresh</button>
          </div>
        </div>
      )}
    </div>
  );
}
