import React, { useState } from 'react';

const MENU = {
  breakfast: [
    { id: 1, name: 'Pancakes', desc: 'Fluffy pancakes with syrup', price: 250, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 4.5, ingredients: ['Flour', 'Eggs', 'Milk', 'Sugar', 'Butter', 'Syrup'] },
    { id: 2, name: 'Omelette', desc: 'Cheese & veggie omelette', price: 200, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.2, ingredients: ['Eggs', 'Cheese', 'Bell Pepper', 'Onion', 'Salt', 'Pepper'] },
  ],
  brunch: [
    { id: 3, name: 'Avocado Toast', desc: 'Sourdough with avocado', price: 300, img: 'https://images.unsplash.com/photo-1464306208224-9c11b7e0b7c7?auto=format&fit=crop&w=400&q=80', rating: 4.7, ingredients: ['Sourdough Bread', 'Avocado', 'Lemon', 'Salt', 'Pepper'] },
    { id: 4, name: 'Fruit Bowl', desc: 'Mixed fresh fruits', price: 180, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.3, ingredients: ['Banana', 'Apple', 'Orange', 'Grapes', 'Melon'] },
  ],
  lunch: [
    { id: 5, name: 'Burger', desc: 'Juicy beef burger', price: 350, img: 'https://images.unsplash.com/photo-1550547660-d9450f859349?auto=format&fit=crop&w=400&q=80', rating: 4.6, ingredients: ['Beef Patty', 'Bun', 'Lettuce', 'Tomato', 'Cheese', 'Onion', 'Sauce'] },
    { id: 6, name: 'Pizza', desc: 'Cheese & tomato pizza', price: 600, img: 'https://images.unsplash.com/photo-1548365328-7e7b7b7b7b7b?auto=format&fit=crop&w=400&q=80', rating: 4.8, ingredients: ['Pizza Dough', 'Tomato Sauce', 'Cheese', 'Basil', 'Olive Oil'] },
  ],
  dinner: [
    { id: 7, name: 'Steak', desc: 'Grilled steak with veggies', price: 900, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.9, ingredients: ['Beef Steak', 'Salt', 'Pepper', 'Vegetables', 'Olive Oil'] },
    { id: 8, name: 'Salmon', desc: 'Baked salmon fillet', price: 850, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 4.7, ingredients: ['Salmon Fillet', 'Lemon', 'Salt', 'Pepper', 'Herbs'] },
  ],
  supper: [
    { id: 9, name: 'Soup', desc: 'Chicken noodle soup', price: 220, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.4, ingredients: ['Chicken', 'Noodles', 'Carrot', 'Celery', 'Onion', 'Broth'] },
    { id: 10, name: 'Sandwich', desc: 'Ham & cheese sandwich', price: 260, img: 'https://images.unsplash.com/photo-1464306208224-9c11b7e0b7c7?auto=format&fit=crop&w=400&q=80', rating: 4.1, ingredients: ['Bread', 'Ham', 'Cheese', 'Butter'] },
  ],
  specials: {
    chef: [
      { id: 11, name: "Chef's Beef Wellington", desc: 'Signature beef wellington', price: 1200, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 5.0, ingredients: ['Beef', 'Puff Pastry', 'Mushrooms', 'Egg', 'Spices'] },
    ],
    day: [
      { id: 12, name: "Day's Seafood Platter", desc: 'Fresh seafood selection', price: 950, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.8, ingredients: ['Shrimp', 'Fish', 'Crab', 'Lemon', 'Herbs'] },
    ],
    vegan: [
      { id: 13, name: 'Vegan Buddha Bowl', desc: 'Plant-based bowl', price: 700, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.9, ingredients: ['Quinoa', 'Chickpeas', 'Avocado', 'Spinach', 'Tahini'] },
    ],
    vegetarian: [
      { id: 14, name: 'Vegetarian Lasagna', desc: 'Cheesy veggie lasagna', price: 650, img: 'https://images.unsplash.com/photo-1548365328-7e7b7b7b7b7b?auto=format&fit=crop&w=400&q=80', rating: 4.7, ingredients: ['Lasagna Noodles', 'Tomato', 'Cheese', 'Spinach', 'Zucchini'] },
    ],
  },
};
const OFFERS = [
  { id: 1, title: '20% Off Pizza', desc: 'Order any pizza and get 20% off!', valid: 'Today only' },
  { id: 2, title: 'Free Soda', desc: 'Get a free soda with any burger', valid: 'This week' },
];

function App() {
  // Placeholder: Replace with actual user account info from auth/profile
  const account = {
    name: 'John Doe',
    address: 'Near Marah Hostel, along Kisii University Gate B road',
    phone: '+254712345678',
  };
  const [page, setPage] = useState('menu');
  const [orderFood, setOrderFood] = useState(null);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [tracking, setTracking] = useState(false);
  const [order, setOrder] = useState({ name: account.name, phone: account.phone, address: account.address, payment: 'Cash', location: { lat: null, lng: null } });
  const [selectedCategory, setSelectedCategory] = useState('breakfast');
  const [showMap, setShowMap] = useState(false);

  const [instructions, setInstructions] = useState({ utensils: [], condiments: [], notes: '' });
  function handleOrder(food) {
    setOrderFood(food);
    setPage('instructions');
  }
  function handleInstructionsSubmit(instr) {
    setInstructions(instr);
    setPage('order');
  }
  function handleOrderSubmit(e) {
    e.preventDefault();
    setOrderPlaced(true);
    setTracking(true);
  }
  function handleLocationChange() {
    // Placeholder: Replace with actual map/location picker logic
    setShowMap(true);
  }
  function handleMapSelect(lat, lng) {
    setOrder({ ...order, location: { lat, lng }, address: `Lat: ${lat}, Lng: ${lng}` });
    setShowMap(false);
  }

  // Viking art background image (unsplash, semi-transparent overlay)
  const vikingBg = 'https://images.unsplash.com/photo-1506744038136-462fa3a6a7f0?auto=format&fit=crop&w=1200&q=80';
  return (
    <div style={{
      fontFamily: 'Inter, Arial, sans-serif',
      minHeight: '100vh',
      position: 'relative',
      overflow: 'hidden',
    }}>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 0,
        backgroundImage: `url(${vikingBg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        opacity: 0.35,
        filter: 'blur(2px)',
      }} />
      <div style={{ position: 'relative', zIndex: 1 }}>
        <header style={{ background: 'rgba(26,34,54,0.92)', color: '#fff', padding: '1.5rem 2rem', display: 'flex', alignItems: 'center', justifyContent: 'space-between', borderRadius: 12, boxShadow: '0 2px 16px #0003', margin: '24px 0' }}>
          <span style={{ fontWeight: 700, fontSize: 32, letterSpacing: 2, fontFamily: 'Cinzel, serif' }}>Floki's Hotel</span>
      <nav>
        <button onClick={() => setPage('menu')} style={navBtn(page === 'menu')}>Menu</button>
        <button onClick={() => setPage('offers')} style={navBtn(page === 'offers')}>Offers</button>
      </nav>
        </header>
        <main style={{ maxWidth: 800, margin: '2rem auto', background: 'rgba(255,255,255,0.92)', borderRadius: 18, boxShadow: '0 2px 24px #0002', padding: '2.5rem', backdropFilter: 'blur(2px)' }}>
          {page === 'menu' && (
            <MenuPage
              menu={MENU[selectedCategory]}
              handleOrder={handleOrder}
              selectedCategory={selectedCategory}
              setSelectedCategory={setSelectedCategory}
            />
          )}
          {page === 'instructions' && orderFood && <InstructionsPage food={orderFood} onSubmit={handleInstructionsSubmit} />}
          {page === 'order' && orderFood && <OrderPage food={orderFood} order={order} setOrder={setOrder} handleOrderSubmit={handleOrderSubmit} orderPlaced={orderPlaced} handleLocationChange={handleLocationChange} handleMapSelect={handleMapSelect} showMap={showMap} instructions={instructions} />}
                  {page === 'track' && <TrackPage tracking={tracking} />}
                  {page === 'offers' && <OffersPage offers={OFFERS} />}
                </main>
              </div>
            </div>
          );
        }
        
        function InstructionsPage({ food, onSubmit }) {
          const [utensils, setUtensils] = useState([]);
          const [condiments, setCondiments] = useState([]);
          const [notes, setNotes] = useState('');
          // Placeholder condiments, to be set by hotel page
          const hotelCondiments = ['Ketchup', 'Mustard', 'Chili Sauce', 'Mayonnaise'];
          function toggleUtensil(ut) {
            setUtensils(utensils.includes(ut) ? utensils.filter(u => u !== ut) : [...utensils, ut]);
          }
          function addCondiment(cond) {
            setCondiments([...condiments, cond]);
          }
          function removeCondiment(idx) {
            setCondiments(condiments.filter((_, i) => i !== idx));
          }
          function handleSubmit(e) {
            e.preventDefault();
            onSubmit({ utensils, condiments, notes });
          }
          return (
            <React.Fragment>
              <h2 style={{ color: '#1a2236' }}>Special Instructions for {food.name}</h2>
              <form onSubmit={handleSubmit} style={{ marginTop: 18 }}>
                <div style={{ marginBottom: 18 }}>
                  <div style={{ fontWeight: 600, marginBottom: 8 }}>Utensils Needed:</div>
                  {['Spoon', 'Plate', 'Cup'].map(ut => (
                    <label key={ut} style={{ marginRight: 18, fontWeight: 500 }}>
                      <input type="checkbox" checked={utensils.includes(ut)} onChange={() => toggleUtensil(ut)} /> {ut}
                    </label>
                  ))}
                </div>
                <div style={{ marginBottom: 18 }}>
                  <div style={{ fontWeight: 600, marginBottom: 8 }}>Condiments:</div>
                  <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
                    {hotelCondiments.map(cond => (
                      <button type="button" key={cond} onClick={() => addCondiment(cond)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '6px 16px', fontWeight: 600, cursor: 'pointer' }}>{cond} +</button>
                    ))}
                  </div>
                  <ul style={{ marginTop: 12, paddingLeft: 18 }}>
                    {condiments.map((cond, idx) => (
                      <li key={idx} style={{ color: '#555', fontSize: 15 }}>
                        {cond} <button type="button" onClick={() => removeCondiment(idx)} style={{ marginLeft: 8, background: '#ffb300', color: '#222', border: 'none', borderRadius: 6, padding: '2px 8px', fontWeight: 600, cursor: 'pointer' }}>Remove</button>
                      </li>
                    ))}
                  </ul>
                </div>
                <div style={{ marginBottom: 18 }}>
                  <div style={{ fontWeight: 600, marginBottom: 8 }}>Additional Notes:</div>
                  <textarea value={notes} onChange={e => setNotes(e.target.value)} style={{ width: '100%', minHeight: 60, borderRadius: 6, border: '1px solid #ccc', fontSize: 16, padding: 8 }} placeholder="Any special instructions?" />
                </div>
                <button type="submit" style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 18px', fontWeight: 600, cursor: 'pointer', marginTop: 8 }}>Continue to Order</button>
              </form>
            </React.Fragment>
          );
        }

function navBtn(active) {
  return {
    background: active ? '#ffb300' : 'transparent',
    color: active ? '#222' : '#fff',
    border: 'none',
    borderRadius: 6,
    marginLeft: 16,
    padding: '8px 18px',
    fontWeight: 600,
    cursor: 'pointer',
    fontSize: 16,
    transition: 'background 0.2s',
  };
}

function MenuPage({ menu, handleOrder, selectedCategory, setSelectedCategory }) {
  const categories = ['breakfast', 'brunch', 'lunch', 'dinner', 'supper', 'specials'];
  const [openIngredients, setOpenIngredients] = useState({});
  const [specialsType, setSpecialsType] = useState('chef');
  const [selectingQty, setSelectingQty] = useState(null); // food.id
  const [qty, setQty] = useState(1);
  function toggleIngredients(id) {
    setOpenIngredients(prev => ({ ...prev, [id]: !prev[id] }));
  }
  let foods = menu;
  if (selectedCategory === 'specials') {
    foods = MENU.specials[specialsType];
  }
  return <>
    <h2 style={{ color: '#1a2236' }}>Menu</h2>
    <div style={{ marginBottom: 24, display: 'flex', gap: 12, justifyContent: 'center', flexWrap: 'wrap' }}>
      {categories.map(cat => (
        <button
          key={cat}
          onClick={() => setSelectedCategory(cat)}
          style={{
            background: selectedCategory === cat ? '#ffb300' : '#f7f7fa',
            color: selectedCategory === cat ? '#222' : '#555',
            border: 'none',
            borderRadius: 6,
            padding: '8px 18px',
            fontWeight: 600,
            cursor: 'pointer',
            fontSize: 16,
            transition: 'background 0.2s',
            minWidth: 120,
          }}
        >
          {cat.charAt(0).toUpperCase() + cat.slice(1)}
        </button>
      ))}
    </div>
    {selectedCategory === 'specials' && (
      <div style={{ marginBottom: 24, display: 'flex', gap: 12, justifyContent: 'center', flexWrap: 'wrap' }}>
        {['chef', 'day', 'vegan', 'vegetarian'].map(type => (
          <button
            key={type}
            onClick={() => setSpecialsType(type)}
            style={{
              background: specialsType === type ? '#ffb300' : '#f7f7fa',
              color: specialsType === type ? '#222' : '#555',
              border: 'none',
              borderRadius: 6,
              padding: '8px 18px',
              fontWeight: 600,
              cursor: 'pointer',
              fontSize: 16,
              transition: 'background 0.2s',
              minWidth: 120,
            }}
          >
            {type === 'chef' ? "Chef's Special" : type === 'day' ? "Day's Special" : type.charAt(0).toUpperCase() + type.slice(1)}
          </button>
        ))}
      </div>
    )}
    <ul style={{
      listStyle: 'none',
      padding: 0,
      margin: 0,
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
      gap: 32,
      width: '100%',
    }}>
      {foods.map(food => (
        <li key={food.id} style={{
          background: '#f7f7fa',
          borderRadius: 12,
          padding: 20,
          boxShadow: '0 1px 8px #0001',
          display: 'flex',
          alignItems: 'center',
          minHeight: 120,
          flexDirection: 'column',
        }}>
          <div style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
            <img src={food.img} alt={food.name} style={{ width: 90, height: 90, objectFit: 'cover', borderRadius: 10, marginRight: 20 }} />
            <div style={{ flex: 1 }}>
              <div style={{ fontWeight: 700, fontSize: 20 }}>{food.name}</div>
              <div style={{ color: '#555', marginBottom: 6 }}>{food.desc}</div>
              <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 17 }}>Ksh {food.price}</div>
              <div style={{ margin: '6px 0' }}>
                <span style={{ color: '#f39c12', fontWeight: 600 }}>Rating: {food.rating} </span>
              </div>
              {selectingQty === food.id ? (
                <div style={{ display: 'flex', alignItems: 'center', marginTop: 8 }}>
                  <button onClick={() => setQty(qty > 1 ? qty - 1 : 1)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '6px 16px', fontWeight: 600, cursor: 'pointer', fontSize: 18 }}>-</button>
                  <span style={{ margin: '0 12px', fontWeight: 700, fontSize: 18 }}>{qty}</span>
                  <button onClick={() => setQty(qty + 1)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '6px 16px', fontWeight: 600, cursor: 'pointer', fontSize: 18 }}>+</button>
                  <button onClick={() => handleOrder({ ...food, quantity: qty })} style={{ marginLeft: 18, background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 18px', fontWeight: 600, cursor: 'pointer' }}>Order</button>
                </div>
              ) : (
                <button onClick={() => { setSelectingQty(food.id); setQty(1); }} style={{ marginTop: 8, background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 18px', fontWeight: 600, cursor: 'pointer' }}>Order</button>
              )}
              <button onClick={() => toggleIngredients(food.id)} style={{ marginTop: 8, background: openIngredients[food.id] ? '#ffb300' : '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '6px 16px', fontWeight: 600, cursor: 'pointer' }}>See Ingredients</button>
            </div>
          </div>
          {openIngredients[food.id] && (
            <div style={{ marginTop: 12, width: '100%' }}>
              <div style={{ fontWeight: 600, color: '#1a2236', marginBottom: 6 }}>Ingredients:</div>
              <ul style={{ paddingLeft: 18, margin: 0 }}>
                {food.ingredients.map((ing, idx) => (
                  <li key={idx} style={{ color: '#555', fontSize: 15 }}>{ing}</li>
                ))}
              </ul>
            </div>
          )}
        </li>
      ))}
    </ul>
  </>;
}

function OrderPage({ food, order, setOrder, handleOrderSubmit, orderPlaced, handleLocationChange, handleMapSelect, showMap, instructions }) {
  const [mpesaStatus, setMpesaStatus] = useState(null);
  async function handleMpesaPay(e) {
    e.preventDefault();
    setMpesaStatus('Processing...');
    try {
      // Replace with actual till number from backend or admin config
      const tillNumber = '123456';
      const response = await fetch('/api/admin/mpesa/pay', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          amount: food.price,
          phone: order.phone,
          tillNumber,
        }),
      });
      const data = await response.json();
      if (data.success) {
        setMpesaStatus('Payment prompt sent. Enter your PIN on your phone.');
      } else {
        setMpesaStatus('Payment failed: ' + (data.error || 'Unknown error'));
      }
    } catch (err) {
      setMpesaStatus('Payment failed: ' + err.message);
    }
  }
  return (
    <React.Fragment>
      <h2 style={{ color: '#1a2236' }}>Order {food.name}</h2>
      <div style={{ display: 'flex', alignItems: 'center', gap: 20, marginBottom: 24 }}>
        <img src={food.img} alt={food.name} style={{ width: 120, height: 120, objectFit: 'cover', borderRadius: 10 }} />
        <div>
          <div style={{ fontWeight: 700, fontSize: 24 }}>{food.name}</div>
          <div style={{ color: '#555', marginBottom: 12 }}>{food.desc}</div>
          <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 20 }}>Ksh {food.price}</div>
          <div style={{ margin: '6px 0' }}>
            <span style={{ color: '#f39c12', fontWeight: 600 }}>Rating: {food.rating} </span>
          </div>
        </div>
      </div>
      {!orderPlaced && (
        <form onSubmit={order.payment === 'M-Pesa' ? handleMpesaPay : handleOrderSubmit} style={{ marginTop: 18 }}>
          <input required placeholder="Your Name" value={order.name} onChange={e => setOrder({ ...order, name: e.target.value })} style={inputStyle} />
          <input required placeholder="Phone" value={order.phone} onChange={e => setOrder({ ...order, phone: e.target.value })} style={inputStyle} />
          <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
            <input required placeholder="Delivery Address" value={order.address} onChange={e => setOrder({ ...order, address: e.target.value })} style={{ ...inputStyle, flex: 1 }} />
            <button type="button" onClick={handleLocationChange} style={{ background: '#ffb300', color: '#222', border: 'none', borderRadius: 6, padding: '8px 12px', fontWeight: 600, cursor: 'pointer' }}>Select Location</button>
          </div>
          {showMap && (
            <div style={{ margin: '12px 0', background: '#e0e0e0', borderRadius: 8, padding: 12 }}>
              <span style={{ fontWeight: 600 }}>Map picker placeholder</span>
              <button type="button" onClick={() => handleMapSelect(-1.2921, 36.8219)} style={{ marginLeft: 12, background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 16px', fontWeight: 600, cursor: 'pointer' }}>Use Current Location</button>
            </div>
          )}
          <div style={{ margin: '12px 0' }}>
            <label style={{ fontWeight: 600, marginRight: 12 }}>Payment Method:</label>
            <select value={order.payment} onChange={e => setOrder({ ...order, payment: e.target.value })} style={{ padding: '8px', borderRadius: 6, border: '1px solid #ccc', fontSize: 16 }}>
              <option value="Cash">Cash</option>
              <option value="M-Pesa">M-Pesa</option>
              <option value="Card">Card</option>
            </select>
          </div>
          {order.payment === 'M-Pesa' ? (
            <button type="submit" style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 18px', fontWeight: 600, cursor: 'pointer', marginTop: 8 }}>Pay with Till</button>
          ) : (
            <button type="submit" style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 18px', fontWeight: 600, cursor: 'pointer', marginTop: 8 }}>Place Order</button>
          )}
        </form>
      )}
      {mpesaStatus && <div style={{ color: '#007bff', fontWeight: 600, marginTop: 18 }}>{mpesaStatus}</div>}
      {orderPlaced && <div style={{ color: '#28a745', fontWeight: 700, marginTop: 18 }}>Order placed! Tracking will be available soon.</div>}
    </React.Fragment>
  );
}

const inputStyle = {
  display: 'block',
  margin: '8px 0',
  padding: '8px',
  borderRadius: 6,
  border: '1px solid #ccc',
  width: '100%',
  fontSize: 16,
};

function TrackPage({ tracking }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Track Delivery</h2>
    {tracking ? <>
      <div style={{ margin: '24px 0', background: '#f7f7fa', borderRadius: 8, padding: 24, textAlign: 'center', color: '#555' }}>
        <span style={{ fontWeight: 600 }}>Live tracking coming soon!</span>
        <div style={{ marginTop: 12 }}>
          <img src="https://maps.googleapis.com/maps/api/staticmap?center=-1.2921,36.8219&zoom=13&size=400x200&markers=color:red|-1.2921,36.8219" alt="Map" style={{ borderRadius: 8, width: '100%', maxWidth: 400 }} />
        </div>
      </div>
    </> : <p>No order placed yet. Place an order to track delivery.</p>}
  </>;
}

function OffersPage({ offers }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Offers</h2>
    <ul style={{ listStyle: 'none', padding: 0 }}>
      {offers.map(offer => (
        <li key={offer.id} style={{ marginBottom: 18, background: '#f7f7fa', borderRadius: 8, padding: 16, boxShadow: '0 1px 6px #0001' }}>
          <div style={{ fontWeight: 700, fontSize: 18 }}>{offer.title}</div>
          <div style={{ color: '#555', marginBottom: 6 }}>{offer.desc}</div>
          <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 16 }}>{offer.valid}</div>
        </li>
      ))}
    </ul>
  </>;
}

export default App;
