import React from 'react';

function Orders({ orders }) {
  return (
    <div style={{ maxWidth: 700, margin: '2rem auto', background: '#fff', borderRadius: 24, boxShadow: '0 4px 32px #0002', padding: '2.5rem', minHeight: 300, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <h2 style={{ color: '#1a2236', fontWeight: 700, fontSize: 28, marginBottom: 18, textAlign: 'center' }}>My Orders</h2>
      <div style={{ fontSize: 24, color: '#ffb300', fontWeight: 700, marginBottom: 18 }}>hello</div>
      {orders && orders.length === 0 ? (
        <div style={{ color: '#888', fontSize: 22, fontWeight: 600, textAlign: 'center', marginTop: 60 }}>You have no orders.</div>
      ) : (
        <>
          <div style={{ fontWeight: 700, fontSize: 22, color: '#1a2236', marginBottom: 14, textAlign: 'center' }}>Past Orders</div>
          <ul style={{ listStyle: 'none', padding: 0, width: '100%' }}>
            {orders.map((order, idx) => (
              <li key={idx} style={{ marginBottom: 24, background: '#f7f7fa', borderRadius: 12, padding: '18px', boxShadow: '0 1px 8px #0001', width: '100%' }}>
                <div style={{ fontWeight: 700, fontSize: 20, color: '#1a2236', marginBottom: 8 }}>Order #{idx + 1} - {order.status}</div>
                <div style={{ color: '#555', fontSize: 16, marginBottom: 6 }}>Placed: {order.date ? new Date(order.date).toLocaleString() : ''}</div>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                  {order.items && order.items.map((item, i) => (
                    <li key={i} style={{ marginBottom: 8, display: 'flex', alignItems: 'center' }}>
                      <img src={item.img || 'https://via.placeholder.com/40?text=No+Image'} alt={item.name} style={{ width: 40, height: 40, objectFit: 'cover', borderRadius: 8, marginRight: 12 }} />
                      <div style={{ fontWeight: 600, fontSize: 16, color: '#1a2236' }}>{item.name}</div>
                      <div style={{ color: '#888', fontSize: 15, marginLeft: 12 }}>Qty: {item.qty}</div>
                      <div style={{ fontWeight: 700, color: '#ffb300', fontSize: 15, marginLeft: 12 }}>{item.total} Ksh</div>
                    </li>
                  ))}
                </ul>
                <div style={{ fontWeight: 700, color: '#ffb300', fontSize: 18, marginTop: 8 }}>Total: {order.items ? order.items.reduce((sum, item) => sum + item.total, 0) : 0} Ksh</div>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default Orders;
