import React, { useState } from 'react';

const MENU = {
  breakfast: [
    { id: 1, name: 'Pancakes', desc: 'Fluffy pancakes with syrup', price: 250, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 4.5, ingredients: ['Flour', 'Eggs', 'Milk', 'Sugar', 'Butter', 'Syrup'] },
    { id: 2, name: 'Omelette', desc: 'Cheese & veggie omelette', price: 200, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.2, ingredients: ['Eggs', 'Cheese', 'Bell Pepper', 'Onion', 'Salt', 'Pepper'] },
  ],
  brunch: [
    { id: 3, name: 'Avocado Toast', desc: 'Sourdough with avocado', price: 300, img: 'https://images.unsplash.com/photo-1464306208224-9c11b7e0b7c7?auto=format&fit=crop&w=400&q=80', rating: 4.7, ingredients: ['Sourdough Bread', 'Avocado', 'Lemon', 'Salt', 'Pepper'] },
    { id: 4, name: 'Fruit Bowl', desc: 'Mixed fresh fruits', price: 180, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.3, ingredients: ['Banana', 'Apple', 'Orange', 'Grapes', 'Melon'] },
  ],
  lunch: [
    { id: 5, name: 'Burger', desc: 'Juicy beef burger', price: 350, img: 'https://images.unsplash.com/photo-1550547660-d9450f859349?auto=format&fit=crop&w=400&q=80', rating: 4.6, ingredients: ['Beef Patty', 'Bun', 'Lettuce', 'Tomato', 'Cheese', 'Onion', 'Sauce'] },
    { id: 6, name: 'Pizza', desc: 'Cheese & tomato pizza', price: 600, img: 'https://images.unsplash.com/photo-1548365328-7e7b7b7b7b7b?auto=format&fit=crop&w=400&q=80', rating: 4.8, ingredients: ['Pizza Dough', 'Tomato Sauce', 'Cheese', 'Basil', 'Olive Oil'] },
  ],
  dinner: [
    { id: 7, name: 'Steak', desc: 'Grilled steak with veggies', price: 900, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.9, ingredients: ['Beef Steak', 'Salt', 'Pepper', 'Vegetables', 'Olive Oil'] },
    { id: 8, name: 'Salmon', desc: 'Baked salmon fillet', price: 850, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 4.7, ingredients: ['Salmon Fillet', 'Lemon', 'Salt', 'Pepper', 'Herbs'] },
  ],
  supper: [
    { id: 9, name: 'Soup', desc: 'Chicken noodle soup', price: 220, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.4, ingredients: ['Chicken', 'Noodles', 'Carrot', 'Celery', 'Onion', 'Broth'] },
    { id: 10, name: 'Sandwich', desc: 'Ham & cheese sandwich', price: 260, img: 'https://images.unsplash.com/photo-1464306208224-9c11b7e0b7c7?auto=format&fit=crop&w=400&q=80', rating: 4.1, ingredients: ['Bread', 'Ham', 'Cheese', 'Butter'] },
  ],
  specials: {
    chef: [
      { id: 11, name: "Chef's Beef Wellington", desc: 'Signature beef wellington', price: 1200, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 5.0, ingredients: ['Beef', 'Puff Pastry', 'Mushrooms', 'Egg', 'Spices'] },
    ],
    day: [
      { id: 12, name: "Day's Seafood Platter", desc: 'Fresh seafood selection', price: 950, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.8, ingredients: ['Shrimp', 'Fish', 'Crab', 'Lemon', 'Herbs'] },
    ],
    vegan: [
      { id: 13, name: 'Vegan Buddha Bowl', desc: 'Plant-based bowl', price: 700, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.9, ingredients: ['Quinoa', 'Chickpeas', 'Avocado', 'Spinach', 'Tahini'] },
    ],
    vegetarian: [
      { id: 14, name: 'Vegetarian Lasagna', desc: 'Cheesy veggie lasagna', price: 650, img: 'https://images.unsplash.com/photo-1548365328-7e7b7b7b7b7b?auto=format&fit=crop&w=400&q=80', rating: 4.7, ingredients: ['Lasagna Noodles', 'Tomato', 'Cheese', 'Spinach', 'Zucchini'] },
    ],
  },
};
const OFFERS = [
  { id: 1, title: '20% Off Pizza', desc: 'Order any pizza and get 20% off!', valid: 'Today only' },
  { id: 2, title: 'Free Soda', desc: 'Get a free soda with any burger', valid: 'This week' },
];

function App() {
  // Placeholder: Replace with actual user account info from auth/profile
  const account = {
    name: 'John Doe',
    address: 'Near Marah Hostel, along Kisii University Gate B road',
    phone: '+254712345678',
  };
  const [page, setPage] = useState('menu');
  const [orderFood, setOrderFood] = useState(null);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [tracking, setTracking] = useState(false);
  const [order, setOrder] = useState({ name: account.name, phone: account.phone, address: account.address, payment: 'Cash', location: { lat: null, lng: null } });
  const [selectedCategory, setSelectedCategory] = useState('breakfast');
  const [showMap, setShowMap] = useState(false);

  const [instructions, setInstructions] = useState({ utensils: [], condiments: [], notes: '' });
  const [cart, setCart] = useState([]);
  const [cartNotification, setCartNotification] = useState('');
  function handleOrder(food) {
    setOrderFood(food);
    setPage('instructions');
  }
  function handleInstructionsSubmit(instr) {
    // Immediately add item to cart and return to menu
    const utensilPrices = { Spoon: 20, Plate: 50, Cup: 20 };
    const utensilCost = instr.utensils.reduce((sum, ut) => sum + (utensilPrices[ut] || 0), 0);
    const qty = instr.qty || 1;
    const total = qty * orderFood.price + utensilCost;
    setCart([...cart, {
      name: orderFood.name,
      qty,
      price: orderFood.price,
      utensils: instr.utensils,
      condiments: instr.condiments,
      notes: instr.notes,
      utensilCost,
      total,
      img: orderFood.img,
    }]);
    // Compose notification listing what was added
    let notif = `${qty} x ${orderFood.name}`;
    if (instr.utensils.length) notif += `, Utensils: ${instr.utensils.join(', ')}`;
    if (instr.condiments.length) notif += `, Condiments: ${instr.condiments.join(', ')}`;
    if (instr.notes) notif += `, Notes: ${instr.notes}`;
    setCartNotification(`${notif} added to cart.`);
    setTimeout(() => setCartNotification(''), 2200);
    setPage('menu');
    setOrderFood(null);
    setInstructions({ utensils: [], condiments: [], notes: '' });
    // Reset MenuPage selectingQty state if needed
    if (typeof window !== 'undefined') {
      setTimeout(() => {
        const evt = new CustomEvent('resetSelectingQty');
        window.dispatchEvent(evt);
      }, 250);
    }
  }
  function handleOrderSubmit(e) {
    e.preventDefault();
    // Add item to cart and skip name/location module
    const utensilPrices = { Spoon: 20, Plate: 50, Cup: 20 };
    const utensilCost = instructions.utensils.reduce((sum, ut) => sum + (utensilPrices[ut] || 0), 0);
    const qty = orderFood.qty || instructions.qty || 1;
    const total = qty * orderFood.price + utensilCost;
    setCart([...cart, {
      name: orderFood.name,
      qty,
      price: orderFood.price,
      utensils: instructions.utensils,
      condiments: instructions.condiments,
      notes: instructions.notes,
      utensilCost,
      total,
      img: orderFood.img,
    }]);
    // Compose notification listing what was added
    let notif = `${qty} x ${orderFood.name}`;
    if (instructions.utensils.length) notif += `, Utensils: ${instructions.utensils.join(', ')}`;
    if (instructions.condiments.length) notif += `, Condiments: ${instructions.condiments.join(', ')}`;
    if (instructions.notes) notif += `, Notes: ${instructions.notes}`;
    setCartNotification(`${notif} added to cart.`);
    setTimeout(() => setCartNotification(''), 2200);
    setPage('menu');
    setOrderFood(null);
    setInstructions({ utensils: [], condiments: [], notes: '' });
    // Reset MenuPage selectingQty state if needed
    if (typeof window !== 'undefined') {
      setTimeout(() => {
        const evt = new CustomEvent('resetSelectingQty');
        window.dispatchEvent(evt);
      }, 250);
    }
  }
  function handleLocationChange() {
    // Placeholder: Replace with actual map/location picker logic
    setShowMap(true);
  }
  function handleMapSelect(lat, lng) {
    setOrder({ ...order, location: { lat, lng }, address: `Lat: ${lat}, Lng: ${lng}` });
    setShowMap(false);
  }

  // Viking art background image (unsplash, semi-transparent overlay)
  const vikingBg = 'https://images.unsplash.com/photo-1506744038136-462fa3a6a7f0?auto=format&fit=crop&w=1200&q=80';
  return (
    <div style={{
      fontFamily: 'Inter, Arial, sans-serif',
      minHeight: '100vh',
      position: 'relative',
      overflow: 'hidden',
    }}>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: 0,
        backgroundImage: `url(${vikingBg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        opacity: 0.35,
        filter: 'blur(2px)',
      }} />
      <div style={{ position: 'relative', zIndex: 1, height: '100vh', overflow: 'hidden' }}>
        <header style={{ position: 'fixed', top: 0, left: 0, width: '100vw', maxWidth: '100vw', background: 'rgba(26,34,54,0.98)', color: '#fff', padding: '1.2rem 1.2rem', display: 'flex', alignItems: 'center', justifyContent: 'space-between', boxShadow: '0 2px 16px #0003', zIndex: 10, borderRadius: 0, fontSize: '1.2rem', flexWrap: 'wrap', boxSizing: 'border-box', overflow: 'hidden' }}>
          <span style={{ fontWeight: 700, fontSize: 32, letterSpacing: 2, fontFamily: 'Cinzel, serif', flexShrink: 0 }}>Floki's Hotel</span>
          <nav style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: 24, fontSize: 22, flexShrink: 1, minWidth: 0 }}>
            <button onClick={() => setPage('menu')} style={{ background: page === 'menu' ? '#ffb300' : 'transparent', color: page === 'menu' ? '#222' : '#fff', border: 'none', fontWeight: 600, cursor: 'pointer', fontSize: 22, padding: '8px 14px', borderRadius: 8, transition: 'background 0.2s', whiteSpace: 'nowrap', minWidth: 0 }}>Menu</button>
            <button onClick={() => setPage('offers')} style={{ background: page === 'offers' ? '#ffb300' : 'transparent', color: page === 'offers' ? '#222' : '#fff', border: 'none', fontWeight: 600, cursor: 'pointer', fontSize: 22, padding: '8px 14px', borderRadius: 8, transition: 'background 0.2s', whiteSpace: 'nowrap', minWidth: 0 }}>Offers</button>
            <button onClick={() => setPage('cart')} style={{ background: page === 'cart' ? '#ffb300' : 'transparent', color: page === 'cart' ? '#222' : '#fff', border: 'none', fontWeight: 600, cursor: 'pointer', fontSize: 22, padding: '8px 14px', borderRadius: 8, transition: 'background 0.2s', whiteSpace: 'nowrap', minWidth: 0 }}>Cart</button>
            <button onClick={() => setPage('orders')} style={{ background: page === 'orders' ? '#ffb300' : 'transparent', color: page === 'orders' ? '#222' : '#fff', border: 'none', fontWeight: 600, cursor: 'pointer', fontSize: 22, padding: '8px 14px', borderRadius: 8, transition: 'background 0.2s', whiteSpace: 'nowrap', minWidth: 0 }}>Orders</button>
          </nav>
        </header>
        <main style={{ maxWidth: 900, width: '95vw', margin: '6.5rem auto 0 auto', background: 'rgba(255,255,255,0.92)', borderRadius: 18, boxShadow: '0 2px 24px #0002', padding: '2.5rem', backdropFilter: 'blur(2px)', height: 'calc(100vh - 6.5rem)', overflowY: 'auto', boxSizing: 'border-box', position: 'relative' }}>
          {cartNotification && (
            <div
              style={{
                position: 'fixed',
                bottom: 32,
                right: 32,
                background: '#ffb300',
                color: '#222',
                fontWeight: 600,
                fontSize: 18,
                borderRadius: 8,
                padding: '12px 28px',
                boxShadow: '0 2px 8px #0002',
                zIndex: 100,
                opacity: 1,
                transition: 'opacity 1s',
                animation: 'fadeoutnotif 2.2s forwards',
              }}
            >
              {cartNotification}
              <style>{`
                @keyframes fadeoutnotif {
                  0% { opacity: 1; }
                  80% { opacity: 1; }
                  100% { opacity: 0; }
                }
              `}</style>
            </div>
          )}
          {page === 'menu' && (
            <MenuPage
              menu={MENU[selectedCategory]}
              handleOrder={handleOrder}
              selectedCategory={selectedCategory}
              setSelectedCategory={setSelectedCategory}
            />
          )}
          {page === 'instructions' && orderFood && <InstructionsPage food={orderFood} onSubmit={handleInstructionsSubmit} />}
          {/* OrderPage and name/location/payment module fully removed. Only allow adding to cart from instructions page. */}
          {page === 'track' && <TrackPage tracking={tracking} />}
          {page === 'offers' && <OffersPage offers={OFFERS} />}
        {page === 'cart' && (
          <CartPage cart={cart} setCart={setCart} />
        )}
        </main>
      </div>
            </div>
          );
        }
        
        function InstructionsPage({ food, onSubmit }) {
          const [utensils, setUtensils] = useState([]);
          const [condiments, setCondiments] = useState([]);
          const [notes, setNotes] = useState('');
          const [qty, setQty] = useState(food.quantity || 1);
          // Placeholder condiments, to be set by hotel page
          const hotelCondiments = ['Ketchup', 'Mustard', 'Chili Sauce', 'Mayonnaise'];
          const utensilPrices = { Spoon: 20, Plate: 50, Cup: 20 };
          
          function toggleUtensil(ut) {
            setUtensils(utensils.includes(ut) ? utensils.filter(u => u !== ut) : [...utensils, ut]);
          }
          
          function addCondiment(cond) {
            setCondiments([...condiments, cond]);
          }
          
          function removeCondiment(idx) {
            setCondiments(condiments.filter((_, i) => i !== idx));
          }
          
          function handleSubmit(e) {
            e.preventDefault();
            onSubmit({ utensils, condiments, notes, qty });
          }
          
          const utensilCost = utensils.reduce((sum, ut) => sum + (utensilPrices[ut] || 0), 0);
          const total = (qty * food.price) + utensilCost;
          
          return (
            <div style={{ maxWidth: 520, margin: '2rem auto', background: '#fff', borderRadius: 24, boxShadow: '0 4px 32px #0002', padding: '2.5rem', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <img src={food.img || 'https://via.placeholder.com/180?text=No+Image'} alt={food.name} style={{ width: 180, height: 180, objectFit: 'cover', borderRadius: 16, marginBottom: 18, boxShadow: '0 2px 12px #0001' }} />
              <div style={{ fontWeight: 700, fontSize: 30, marginBottom: 8, color: '#1a2236', letterSpacing: 1 }}>{food.name}</div>
              <div style={{ color: '#555', marginBottom: 12, fontSize: 18 }}>{food.desc}</div>
              <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 22, marginBottom: 6 }}>Ksh {food.price}</div>
              <span style={{ color: '#f39c12', fontWeight: 600, fontSize: 17, marginBottom: 18 }}>Rating: {food.rating} </span>
              <form onSubmit={handleSubmit} style={{ width: '100%' }}>
                <div style={{ marginBottom: 24 }}>
                  <div style={{ fontWeight: 600, marginBottom: 8, color: '#1a2236' }}>Utensils Needed:</div>
                  <div style={{ display: 'flex', gap: 18, marginBottom: 8 }}>
                    {['Spoon', 'Plate', 'Cup'].map(ut => (
                      <label key={ut} style={{ fontWeight: 500, display: 'flex', alignItems: 'center', gap: 6 }}>
                        <input type="checkbox" checked={utensils.includes(ut)} onChange={() => toggleUtensil(ut)} style={{ accentColor: '#ffb300', width: 18, height: 18 }} /> {ut}
                        <span style={{ color: '#888', fontSize: 14, marginLeft: 4 }}>+{utensilPrices[ut]} Ksh</span>
                      </label>
                    ))}
                  </div>
                  {utensils.length > 0 && (
                    <div style={{ color: '#1a2236', fontWeight: 500, fontSize: 15, marginTop: 6 }}>
                      Utensil cost: <span style={{ color: '#ffb300', fontWeight: 700 }}>{utensilCost} Ksh</span>
                    </div>
                  )}
                </div>
                <div style={{ marginBottom: 24 }}>
                  <div style={{ fontWeight: 600, marginBottom: 8, color: '#1a2236' }}>Condiments:</div>
                  <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap', marginBottom: 8 }}>
                    {hotelCondiments.map(cond => (
                      <button type="button" key={cond} onClick={() => addCondiment(cond)} style={{ background: '#f7f7fa', color: '#222', border: '1px solid #ddd', borderRadius: 8, padding: '6px 16px', fontWeight: 600, cursor: 'pointer', fontSize: 16, boxShadow: '0 1px 4px #0001' }}>{cond} +</button>
                    ))}
                  </div>
                  <ul style={{ marginTop: 12, paddingLeft: 18 }}>
                    {condiments.map((cond, idx) => (
                      <li key={idx} style={{ color: '#555', fontSize: 15, marginBottom: 6 }}>
                        {cond} <button type="button" onClick={() => removeCondiment(idx)} style={{ marginLeft: 8, background: '#ffb300', color: '#222', border: 'none', borderRadius: 6, padding: '2px 8px', fontWeight: 600, cursor: 'pointer', fontSize: 14 }}>Remove</button>
                      </li>
                    ))}
                  </ul>
                </div>
                <div style={{ marginBottom: 24 }}>
                  <div style={{ fontWeight: 600, marginBottom: 8, color: '#1a2236' }}>Additional Notes:</div>
                  <textarea value={notes} onChange={e => setNotes(e.target.value)} style={{ width: '100%', minHeight: 60, borderRadius: 10, border: '1px solid #ccc', fontSize: 16, padding: 10, background: '#f7f7fa', marginTop: 4 }} placeholder="Any special instructions?" />
                </div>
                <div style={{ marginBottom: 24, display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 12 }}>
                  <div style={{ fontWeight: 600, color: '#1a2236' }}>Quantity:</div>
                  <button type="button" onClick={() => setQty(qty > 1 ? qty - 1 : 1)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 8, padding: '8px 18px', fontWeight: 700, cursor: 'pointer', fontSize: 20, boxShadow: '0 1px 4px #0001' }}>-</button>
                  <span style={{ margin: '0 12px', fontWeight: 700, fontSize: 20 }}>{qty}</span>
                  <button type="button" onClick={() => setQty(qty + 1)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 8, padding: '8px 18px', fontWeight: 700, cursor: 'pointer', fontSize: 20, boxShadow: '0 1px 4px #0001' }}>+</button>
                </div>
                <button type="submit" style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 10, padding: '14px 28px', fontWeight: 700, fontSize: 18, cursor: 'pointer', marginTop: 8, boxShadow: '0 2px 8px #0002', width: '100%' }}>
                  {`Add to Cart (${qty}) ${total.toLocaleString()} Kshs`}
                </button>
              </form>
            </div>
          );
        }

function navBtn(active) {
  return {
    background: active ? '#ffb300' : 'transparent',
    color: active ? '#222' : '#fff',
    border: 'none',
    borderRadius: 6,
    marginLeft: 16,
    padding: '8px 18px',
    fontWeight: 600,
    cursor: 'pointer',
    fontSize: 16,
    transition: 'background 0.2s',
  };
}

function CartPage({ cart, setCart }) {
    const [deleteIdx, setDeleteIdx] = useState(null);
    function handleDelete(idx) {
      setDeleteIdx(idx);
    }
    function confirmDelete() {
      setCart(cart.filter((_, i) => i !== deleteIdx));
      setDeleteIdx(null);
    }
    function cancelDelete() {
      setDeleteIdx(null);
    }
  const [editingIdx, setEditingIdx] = useState(null);
  const [editItem, setEditItem] = useState(null);
  const utensilPrices = { Spoon: 20, Plate: 50, Cup: 20 };
  function handleDelete(idx) {
    setCart(cart.filter((_, i) => i !== idx));
  }
  function handleEdit(idx) {
    setEditingIdx(idx);
    setEditItem({ ...cart[idx] });
  }
  function handleEditChange(field, value) {
    setEditItem({ ...editItem, [field]: value });
  }
  function toggleUtensil(ut) {
    setEditItem({ ...editItem, utensils: editItem.utensils.includes(ut) ? editItem.utensils.filter(u => u !== ut) : [...editItem.utensils, ut] });
  }
  function addCondiment(cond) {
    setEditItem({ ...editItem, condiments: [...editItem.condiments, cond] });
  }
  function removeCondiment(idx) {
    setEditItem({ ...editItem, condiments: editItem.condiments.filter((_, i) => i !== idx) });
  }
  function handleSaveEdit() {
    const utensilCost = editItem.utensils.reduce((sum, ut) => sum + (utensilPrices[ut] || 0), 0);
    const total = editItem.qty * editItem.price + utensilCost;
    setCart(cart.map((item, idx) => idx === editingIdx ? { ...editItem, utensilCost, total } : item));
    setEditingIdx(null);
    setEditItem(null);
  }
  return (
    <div>
      <h2 style={{ color: '#1a2236', marginBottom: 18 }}>Cart</h2>
      {cart.length === 0 ? (
        <div style={{ color: '#888', fontSize: 18 }}>Cart is empty.</div>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {cart.map((item, idx) => (
            <li key={idx} style={{ background: '#f7f7fa', borderRadius: 12, boxShadow: '0 2px 8px #0001', marginBottom: 18, padding: '18px', display: 'flex', alignItems: 'center', gap: 18 }}>
              <img src={item.img || 'https://via.placeholder.com/80?text=No+Image'} alt={item.name} style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 8 }} />
              <div style={{ flex: 1 }}>
                {editingIdx === idx ? (
                  <div>
                    <div style={{ fontWeight: 700, fontSize: 22 }}>{item.name}</div>
                    <div style={{ color: '#555', fontSize: 16, marginBottom: 8 }}>Qty:
                      <button onClick={() => handleEditChange('qty', editItem.qty > 1 ? editItem.qty - 1 : 1)} style={{ marginLeft: 8, background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '4px 12px', fontWeight: 600, cursor: 'pointer', fontSize: 16 }}>-</button>
                      <span style={{ margin: '0 8px', fontWeight: 700 }}>{editItem.qty}</span>
                      <button onClick={() => handleEditChange('qty', editItem.qty + 1)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '4px 12px', fontWeight: 600, cursor: 'pointer', fontSize: 16 }}>+</button>
                    </div>
                    <div style={{ color: '#555', fontSize: 16, marginBottom: 8 }}>Utensils:
                      {['Spoon', 'Plate', 'Cup'].map(ut => (
                        <label key={ut} style={{ marginLeft: 12, fontWeight: 500 }}>
                          <input type="checkbox" checked={editItem.utensils.includes(ut)} onChange={() => toggleUtensil(ut)} /> {ut} (+{utensilPrices[ut]} Ksh)
                        </label>
                      ))}
                    </div>
                    <div style={{ color: '#555', fontSize: 16, marginBottom: 8 }}>Condiments:
                      <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap', marginTop: 4 }}>
                        {['Ketchup', 'Mustard', 'Chili Sauce', 'Mayonnaise'].map(cond => (
                          <button type="button" key={cond} onClick={() => addCondiment(cond)} style={{ background: '#f7f7fa', color: '#222', border: '1px solid #ddd', borderRadius: 8, padding: '4px 12px', fontWeight: 600, cursor: 'pointer', fontSize: 14 }}>{cond} +</button>
                        ))}
                      </div>
                      <ul style={{ marginTop: 8, paddingLeft: 18 }}>
                        {editItem.condiments.map((cond, cidx) => (
                          <li key={cidx} style={{ color: '#555', fontSize: 15 }}>
                            {cond} <button type="button" onClick={() => removeCondiment(cidx)} style={{ marginLeft: 8, background: '#ffb300', color: '#222', border: 'none', borderRadius: 6, padding: '2px 8px', fontWeight: 600, cursor: 'pointer', fontSize: 13 }}>Remove</button>
                          </li>
                        ))}
                      </ul>
                    </div>
                    <div style={{ color: '#555', fontSize: 16, marginBottom: 8 }}>Notes:
                      <textarea value={editItem.notes} onChange={e => handleEditChange('notes', e.target.value)} style={{ width: '100%', minHeight: 40, borderRadius: 6, border: '1px solid #ccc', fontSize: 15, padding: 6, marginTop: 4 }} placeholder="Any special instructions?" />
                    </div>
                    <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 18, marginBottom: 8 }}>Total: Ksh {(editItem.qty * editItem.price + editItem.utensils.reduce((sum, ut) => sum + (utensilPrices[ut] || 0), 0)).toLocaleString()}</div>
                    <button onClick={handleSaveEdit} style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 8, padding: '8px 18px', fontWeight: 700, fontSize: 16, cursor: 'pointer', marginRight: 12 }}>Save</button>
                    <button onClick={() => { setEditingIdx(null); setEditItem(null); }} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 8, padding: '8px 18px', fontWeight: 700, fontSize: 16, cursor: 'pointer' }}>Cancel</button>
                  </div>
                ) : (
                  <>
                    <div style={{ fontWeight: 700, fontSize: 22 }}>{item.name}</div>
                    <div style={{ color: '#555', fontSize: 16 }}>Qty: {item.qty}</div>
                    <div style={{ color: '#555', fontSize: 16 }}>Utensils: {item.utensils.join(', ') || 'None'} (Ksh {item.utensilCost})</div>
                    <div style={{ color: '#555', fontSize: 16 }}>Condiments: {item.condiments.join(', ') || 'None'}</div>
                    <div style={{ color: '#555', fontSize: 16 }}>Notes: {item.notes || 'None'}</div>
                    <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 18 }}>Total: Ksh {item.total.toLocaleString()}</div>
                    <button onClick={() => handleEdit(idx)} style={{ background: '#ffb300', color: '#222', border: 'none', borderRadius: 8, padding: '8px 18px', fontWeight: 700, fontSize: 16, cursor: 'pointer', marginRight: 12 }}>Edit</button>
                    <button onClick={() => handleDelete(idx)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 8, padding: '8px 18px', fontWeight: 700, fontSize: 16, cursor: 'pointer' }}>Delete</button>
                  </>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
      <button style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 10, padding: '14px 28px', fontWeight: 700, fontSize: 18, cursor: 'pointer', marginTop: 8, boxShadow: '0 2px 8px #0002' }}>Checkout</button>
    </div>
  );
}

function MenuPage({ menu, handleOrder, selectedCategory, setSelectedCategory }) {
  const categories = ['breakfast', 'brunch', 'lunch', 'dinner', 'supper', 'specials'];
  const [openIngredients, setOpenIngredients] = useState({});
  const [specialsType, setSpecialsType] = useState('chef');
  const [selectingQty, setSelectingQty] = useState(null); // food.id
  const [qty, setQty] = useState(1);
  React.useEffect(() => {
    function resetQty() {
      setSelectingQty(null);
      setQty(1);
    }
    window.addEventListener('resetSelectingQty', resetQty);
    return () => window.removeEventListener('resetSelectingQty', resetQty);
  }, []);
  function toggleIngredients(id) {
    setOpenIngredients(prev => ({ ...prev, [id]: !prev[id] }));
  }
  let foods = menu;
  if (selectedCategory === 'specials') {
    foods = MENU.specials[specialsType];
  }
  return <>
    <h2 style={{ color: '#1a2236' }}>Menu</h2>
    <div style={{ marginBottom: 24, display: 'flex', gap: 12, justifyContent: 'center', flexWrap: 'wrap' }}>
      {categories.map(cat => (
        <button
          key={cat}
          onClick={() => setSelectedCategory(cat)}
          style={{
            background: selectedCategory === cat ? '#ffb300' : '#f7f7fa',
            color: selectedCategory === cat ? '#222' : '#555',
            border: 'none',
            borderRadius: 6,
            padding: '8px 18px',
            fontWeight: 600,
            cursor: 'pointer',
            fontSize: 16,
            transition: 'background 0.2s',
            minWidth: 120,
          }}
        >
          {cat.charAt(0).toUpperCase() + cat.slice(1)}
        </button>
      ))}
    </div>
    {selectedCategory === 'specials' && (
      <div style={{ marginBottom: 24, display: 'flex', gap: 12, justifyContent: 'center', flexWrap: 'wrap' }}>
        {['chef', 'day', 'vegan', 'vegetarian'].map(type => (
          <button
            key={type}
            onClick={() => setSpecialsType(type)}
            style={{
              background: specialsType === type ? '#ffb300' : '#f7f7fa',
              color: specialsType === type ? '#222' : '#555',
              border: 'none',
              borderRadius: 6,
              padding: '8px 18px',
              fontWeight: 600,
              cursor: 'pointer',
              fontSize: 16,
              transition: 'background 0.2s',
              minWidth: 120,
            }}
          >
            {type === 'chef' ? "Chef's Special" : type === 'day' ? "Day's Special" : type.charAt(0).toUpperCase() + type.slice(1)}
          </button>
        ))}
      </div>
    )}
    <ul style={{
      listStyle: 'none',
      padding: 0,
      margin: 0,
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
      gap: 32,
      width: '100%',
    }}>
      {foods.map(food => (
        <li key={food.id} style={{
          background: '#f7f7fa',
          borderRadius: 12,
          boxShadow: '0 2px 8px #0001',
          padding: 18,
          display: 'flex',
          flexDirection: 'column',
          gap: 12,
        }}>
          <img src={food.img || 'https://via.placeholder.com/120?text=No+Image'} alt={food.name} style={{ width: '100%', height: 200, objectFit: 'cover', borderRadius: 8 }} />
          <div style={{ fontWeight: 700, fontSize: 20, color: '#1a2236' }}>{food.name}</div>
          <div style={{ color: '#555', fontSize: 15 }}>{food.desc}</div>
          <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 18 }}>Ksh {food.price}</div>
          <span style={{ color: '#f39c12', fontWeight: 600, fontSize: 15 }}>Rating: {food.rating} </span>
          {selectingQty === food.id ? (
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 12, marginTop: 12 }}>
              <button onClick={() => setQty(qty > 1 ? qty - 1 : 1)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '6px 12px', fontWeight: 700, fontSize: 18 }}>-</button>
              <span style={{ fontWeight: 700, fontSize: 18 }}>{qty}</span>
              <button onClick={() => setQty(qty + 1)} style={{ background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 6, padding: '6px 12px', fontWeight: 700, fontSize: 18 }}>+</button>
              <button onClick={() => { handleOrder({ ...food, quantity: qty }); setSelectingQty(null); setQty(1); }} style={{ background: '#ffb300', color: '#222', border: 'none', borderRadius: 6, padding: '8px 18px', fontWeight: 700, fontSize: 16, cursor: 'pointer', marginLeft: 12 }}>Add</button>
            </div>
          ) : (
            <button onClick={() => setSelectingQty(food.id)} style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 8, padding: '10px 18px', fontWeight: 700, fontSize: 16, cursor: 'pointer', marginTop: 8 }}>Order Now</button>
          )}
          {openIngredients[food.id] && (
            <div style={{ marginTop: 12, width: '100%' }}>
              <div style={{ fontWeight: 600, color: '#1a2236', marginBottom: 6 }}>Ingredients:</div>
              <ul style={{ paddingLeft: 18, margin: 0 }}>
                {food.ingredients.map((ing, idx) => (
                  <li key={idx} style={{ color: '#555', fontSize: 15 }}>{ing}</li>
                ))}
              </ul>
            </div>
          )}
        </li>
      ))}
    </ul>
  </>;
}

function OrderPage({ food, order, setOrder, handleOrderSubmit, orderPlaced, handleLocationChange, handleMapSelect, showMap, instructions }) {
  // OrderPage fully deleted.
}

const inputStyle = {
  display: 'block',
  margin: '8px 0',
  padding: '8px',
  borderRadius: 6,
  border: '1px solid #ccc',
  width: '100%',
  fontSize: 16,
};

function TrackPage({ tracking }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Track Delivery</h2>
    {tracking ? <>
      <div style={{ margin: '24px 0', background: '#f7f7fa', borderRadius: 8, padding: 24, textAlign: 'center', color: '#555' }}>
        <span style={{ fontWeight: 600 }}>Live tracking coming soon!</span>
        <div style={{ marginTop: 12 }}>
          <img src="https://maps.googleapis.com/maps/api/staticmap?center=-1.2921,36.8219&zoom=13&size=400x200&markers=color:red|-1.2921,36.8219" alt="Map" style={{ borderRadius: 8, width: '100%', maxWidth: 400 }} />
        </div>
      </div>
    </> : <p>No order placed yet. Place an order to track delivery.</p>}
  </>;
}

function OffersPage({ offers }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Offers</h2>
    <ul style={{ listStyle: 'none', padding: 0 }}>
      {offers.map(offer => (
        <li key={offer.id} style={{ marginBottom: 18, background: '#f7f7fa', borderRadius: 8, padding: 16, boxShadow: '0 1px 6px #0001' }}>
          <div style={{ fontWeight: 700, fontSize: 18 }}>{offer.title}</div>
          <div style={{ color: '#555', marginBottom: 6 }}>{offer.desc}</div>
          <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 16 }}>{offer.valid}</div>
        </li>
      ))}
    </ul>
  </>;
}

export default App;
