import React, { useState } from 'react';

const MENU = {
  breakfast: [
    { id: 1, name: 'Pancakes', desc: 'Fluffy pancakes with syrup', price: 250, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 4.5 },
    { id: 2, name: 'Omelette', desc: 'Cheese & veggie omelette', price: 200, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.2 },
  ],
  brunch: [
    { id: 3, name: 'Avocado Toast', desc: 'Sourdough with avocado', price: 300, img: 'https://images.unsplash.com/photo-1464306208224-9c11b7e0b7c7?auto=format&fit=crop&w=400&q=80', rating: 4.7 },
    { id: 4, name: 'Fruit Bowl', desc: 'Mixed fresh fruits', price: 180, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.3 },
  ],
  lunch: [
    { id: 5, name: 'Burger', desc: 'Juicy beef burger', price: 350, img: 'https://images.unsplash.com/photo-1550547660-d9450f859349?auto=format&fit=crop&w=400&q=80', rating: 4.6 },
    { id: 6, name: 'Pizza', desc: 'Cheese & tomato pizza', price: 600, img: 'https://images.unsplash.com/photo-1548365328-7e7b7b7b7b7b?auto=format&fit=crop&w=400&q=80', rating: 4.8 },
  ],
  dinner: [
    { id: 7, name: 'Steak', desc: 'Grilled steak with veggies', price: 900, img: 'https://images.unsplash.com/photo-1519864606261-3c7b7b8b7c7b?auto=format&fit=crop&w=400&q=80', rating: 4.9 },
    { id: 8, name: 'Salmon', desc: 'Baked salmon fillet', price: 850, img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80', rating: 4.7 },
  ],
  supper: [
    { id: 9, name: 'Soup', desc: 'Chicken noodle soup', price: 220, img: 'https://images.unsplash.com/photo-1465101046530-73398c7f1b58?auto=format&fit=crop&w=400&q=80', rating: 4.4 },
    { id: 10, name: 'Sandwich', desc: 'Ham & cheese sandwich', price: 260, img: 'https://images.unsplash.com/photo-1464306208224-9c11b7e0b7c7?auto=format&fit=crop&w=400&q=80', rating: 4.1 },
  ],
};
const OFFERS = [
  { id: 1, title: '20% Off Pizza', desc: 'Order any pizza and get 20% off!', valid: 'Today only' },
  { id: 2, title: 'Free Soda', desc: 'Get a free soda with any burger', valid: 'This week' },
];

function App() {
  const [page, setPage] = useState('menu');
  const [orderFood, setOrderFood] = useState(null);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [tracking, setTracking] = useState(false);
  const [order, setOrder] = useState({ name: '', phone: '', address: '' });

  function handleOrder(food) {
    setOrderFood(food);
    setPage('order');
  }
  function handleOrderSubmit(e) {
    e.preventDefault();
    setOrderPlaced(true);
    setTracking(true);
  }

  return (
    <div style={{ fontFamily: 'Inter, Arial, sans-serif', background: '#f7f7fa', minHeight: '100vh' }}>
      <header style={{ background: '#1a2236', color: '#fff', padding: '1.5rem 2rem', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <span style={{ fontWeight: 700, fontSize: 28 }}>Floki's Hotel - Client</span>
        <nav>
          <button onClick={() => setPage('menu')} style={navBtn(page === 'menu')}>Menu</button>
          <button onClick={() => setPage('track')} style={navBtn(page === 'track')}>Track</button>
          <button onClick={() => setPage('offers')} style={navBtn(page === 'offers')}>Offers</button>
        </nav>
      </header>
      <main style={{ maxWidth: 700, margin: '2rem auto', background: '#fff', borderRadius: 12, boxShadow: '0 2px 16px #0001', padding: '2rem' }}>
        {page === 'menu' && <MenuPage menu={MENU} handleOrder={handleOrder} />}
        {page === 'order' && orderFood && <OrderPage food={orderFood} order={order} setOrder={setOrder} handleOrderSubmit={handleOrderSubmit} orderPlaced={orderPlaced} />}
        {page === 'track' && <TrackPage tracking={tracking} />}
        {page === 'offers' && <OffersPage offers={OFFERS} />}
      </main>
      <footer style={{ textAlign: 'center', color: '#888', padding: 24, fontSize: 15 }}>
        &copy; {new Date().getFullYear()} Floki's Hotel. All rights reserved.
      </footer>
    </div>
  );
}

function navBtn(active) {
  return {
    background: active ? '#ffb300' : 'transparent',
    color: active ? '#222' : '#fff',
    border: 'none',
    borderRadius: 6,
    marginLeft: 16,
    padding: '8px 18px',
    fontWeight: 600,
    cursor: 'pointer',
    fontSize: 16,
    transition: 'background 0.2s',
  };
}

function MenuPage({ menu, handleOrder }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Menu</h2>
    {Object.entries(menu).map(([category, foods]) => (
      <div key={category} style={{ marginBottom: 32 }}>
        <h3 style={{ color: '#ffb300', marginBottom: 12, textTransform: 'capitalize' }}>{category}</h3>
        <ul style={{ listStyle: 'none', padding: 0, display: 'flex', flexWrap: 'wrap', gap: 24 }}>
          {foods.map(food => (
            <li key={food.id} style={{ width: 300, background: '#f7f7fa', borderRadius: 8, padding: 16, boxShadow: '0 1px 6px #0001', display: 'flex', alignItems: 'center', marginBottom: 18 }}>
              <img src={food.img} alt={food.name} style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 8, marginRight: 16 }} />
              <div style={{ flex: 1 }}>
                <div style={{ fontWeight: 700, fontSize: 18 }}>{food.name}</div>
                <div style={{ color: '#555', marginBottom: 6 }}>{food.desc}</div>
                <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 16 }}>Ksh {food.price}</div>
                <div style={{ margin: '6px 0' }}>
                  <span style={{ color: '#f39c12', fontWeight: 600 }}>Rating: {food.rating} ★</span>
                </div>
                <button onClick={() => handleOrder(food)} style={{ marginTop: 8, background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 16px', fontWeight: 600, cursor: 'pointer' }}>Order</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    ))}
  </>;
}

function OrderPage({ food, order, setOrder, handleOrderSubmit, orderPlaced }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Order Food</h2>
    <div style={{ background: '#f7f7fa', borderRadius: 8, padding: 16, boxShadow: '0 1px 6px #0001', marginBottom: 18, display: 'flex', alignItems: 'center' }}>
      <img src={food.img} alt={food.name} style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 8, marginRight: 16 }} />
      <div style={{ flex: 1 }}>
        <div style={{ fontWeight: 700, fontSize: 18 }}>{food.name}</div>
        <div style={{ color: '#555', marginBottom: 6 }}>{food.desc}</div>
        <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 16 }}>Ksh {food.price}</div>
        <div style={{ margin: '6px 0' }}>
          <span style={{ color: '#f39c12', fontWeight: 600 }}>Rating: {food.rating} ★</span>
        </div>
      </div>
    </div>
    {!orderPlaced && <form onSubmit={handleOrderSubmit} style={{ marginTop: 18 }}>
      <input required placeholder="Your Name" value={order.name} onChange={e => setOrder({ ...order, name: e.target.value })} style={inputStyle} />
      <input required placeholder="Phone" value={order.phone} onChange={e => setOrder({ ...order, phone: e.target.value })} style={inputStyle} />
      <input required placeholder="Delivery Address" value={order.address} onChange={e => setOrder({ ...order, address: e.target.value })} style={inputStyle} />
      <button type="submit" style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 18px', fontWeight: 600, cursor: 'pointer', marginTop: 8 }}>Place Order</button>
    </form>}
    {orderPlaced && <div style={{ color: '#28a745', fontWeight: 700, marginTop: 18 }}>Order placed! Tracking will be available soon.</div>}
  </>;
}

const inputStyle = {
  display: 'block',
  margin: '8px 0',
  padding: '8px',
  borderRadius: 6,
  border: '1px solid #ccc',
  width: '100%',
  fontSize: 16,
};

function TrackPage({ tracking }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Track Delivery</h2>
    {tracking ? <>
      <div style={{ margin: '24px 0', background: '#f7f7fa', borderRadius: 8, padding: 24, textAlign: 'center', color: '#555' }}>
        <span style={{ fontWeight: 600 }}>Live tracking coming soon!</span>
        <div style={{ marginTop: 12 }}>
          <img src="https://maps.googleapis.com/maps/api/staticmap?center=-1.2921,36.8219&zoom=13&size=400x200&markers=color:red|-1.2921,36.8219" alt="Map" style={{ borderRadius: 8, width: '100%', maxWidth: 400 }} />
        </div>
      </div>
    </> : <p>No order placed yet. Place an order to track delivery.</p>}
  </>;
}

function OffersPage({ offers }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Offers</h2>
    <ul style={{ listStyle: 'none', padding: 0 }}>
      {offers.map(offer => (
        <li key={offer.id} style={{ marginBottom: 18, background: '#f7f7fa', borderRadius: 8, padding: 16, boxShadow: '0 1px 6px #0001' }}>
          <div style={{ fontWeight: 700, fontSize: 18 }}>{offer.title}</div>
          <div style={{ color: '#555', marginBottom: 6 }}>{offer.desc}</div>
          <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 16 }}>{offer.valid}</div>
        </li>
      ))}
    </ul>
  </>;
}

export default App;
