import React from 'react';

function Track({ trackOrders }) {
  return (
    <div style={{ maxWidth: 700, margin: '2rem auto', background: '#fff', borderRadius: 24, boxShadow: '0 4px 32px #0002', padding: '2.5rem', minHeight: 300, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <h2 style={{ color: '#1a2236', fontWeight: 700, fontSize: 28, marginBottom: 18, textAlign: 'center' }}>Track Order</h2>
      {trackOrders && trackOrders.length > 0 ? (
        <div style={{ width: '100%' }}>
          {trackOrders.map((order, idx) => (
            <div key={idx} style={{ marginBottom: 32, padding: 18, borderRadius: 12, background: '#f7f7f7', boxShadow: '0 2px 8px #0001' }}>
              <div style={{ fontWeight: 600, fontSize: 20 }}>Order #{idx + 1}</div>
              <div style={{ marginTop: 8 }}>
                <strong>Items:</strong>
                <ul>
                  {order.items.map((item, i) => (
                    <li key={i}>{item.name} x {item.qty}</li>
                  ))}
                </ul>
              </div>
              <div><strong>Delivery Option:</strong> {order.deliveryOption}</div>
              <div><strong>Payment Method:</strong> {order.paymentMethod}</div>
              <div><strong>Total:</strong> KES {order.grandTotal}</div>
              <div style={{ marginTop: 8, color: '#888' }}><em>Status: Preparing...</em></div>
            </div>
          ))}
        </div>
      ) : (
        <div style={{ color: '#888', fontSize: 22, fontWeight: 600, textAlign: 'center', marginTop: 60 }}>
          No tracked orders yet.
        </div>
      )}
    </div>
  );
}

export default Track;
