  width: '100%',
  fontSize: 16,
};

function TrackPage({ tracking }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Track Delivery</h2>
    {tracking ? <>
      <div style={{ margin: '24px 0', background: '#f7f7fa', borderRadius: 8, padding: 24, textAlign: 'center', color: '#555' }}>
        <span style={{ fontWeight: 600 }}>Live tracking coming soon!</span>
        <div style={{ marginTop: 12 }}>
          <img src="https://maps.googleapis.com/maps/api/staticmap?center=-1.2921,36.8219&zoom=13&size=400x200&markers=color:red|-1.2921,36.8219" alt="Map" style={{ borderRadius: 8, width: '100%', maxWidth: 400 }} />
        </div>
      </div>
    </> : <p>No order placed yet. Place an order to track delivery.</p>}
  </>;
}

function OffersPage({ offers }) {
  return <>
    <h2 style={{ color: '#1a2236' }}>Offers</h2>
    <ul style={{ listStyle: 'none', padding: 0 }}>
      {offers.map(offer => (
        <li key={offer.id} style={{ marginBottom: 18, background: '#f7f7fa', borderRadius: 8, padding: 16, boxShadow: '0 1px 6px #0001' }}>
          <div style={{ fontWeight: 700, fontSize: 18 }}>{offer.title}</div>
          <div style={{ color: '#555', marginBottom: 6 }}>{offer.desc}</div>
          <div style={{ fontWeight: 600, color: '#ffb300', fontSize: 16 }}>{offer.valid}</div>
        </li>
      ))}
    </ul>
  </>;
}

function CheckoutPage() {
  return (
    <div style={{ maxWidth: 520, margin: '4rem auto', background: '#fff', borderRadius: 18, boxShadow: '0 4px 32px #0003', padding: '2.5rem', display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: 400 }}>
      <h2 style={{ color: '#1a2236', fontWeight: 700, fontSize: 28, marginBottom: 18 }}>Checkout</h2>
      <div style={{ width: '100%', marginBottom: 18 }}>
        <div style={{ fontWeight: 600, fontSize: 20, marginBottom: 8 }}>Items:</div>
        <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
          {cart.map((item, idx) => (
            <li key={idx} style={{ marginBottom: 14, display: 'flex', alignItems: 'center', background: '#f7f7fa', borderRadius: 10, padding: '12px 14px', boxShadow: '0 1px 6px #0001' }}>
              <img src={item.img || 'https://via.placeholder.com/40?text=No+Image'} alt={item.name} style={{ width: 48, height: 48, objectFit: 'cover', borderRadius: 10, marginRight: 16 }} />
              <div style={{ flex: 1 }}>
                <div style={{ fontWeight: 700, fontSize: 18, color: '#1a2236' }}>{item.name}</div>
                <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
                  <div style={{ color: '#555', fontSize: 15 }}>Qty: {item.qty}</div>
                  <div style={{ fontWeight: 700, color: '#ffb300', fontSize: 16 }}>{item.total} Ksh</div>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div style={{ width: '100%', marginBottom: 18 }}>
        <div style={{ fontWeight: 600, fontSize: 20 }}>Grand Total:</div>
        <div style={{ fontWeight: 700, fontSize: 24, color: '#ffb300', marginTop: 6 }}>{cart.reduce((sum, item) => sum + item.total, 0)} Ksh</div>
      </div>
      <div style={{ width: '100%', marginBottom: 18 }}>
        <label style={{ fontWeight: 600, fontSize: 18, color: '#1a2236', marginBottom: 8 }}>Payment Method:</label>
        <select value={checkoutPayment} onChange={e => setCheckoutPayment(e.target.value)} style={{ width: '100%', padding: '10px', borderRadius: 8, border: '1px solid #ccc', fontSize: 17, marginTop: 6 }}>
          <option value="Cash">Cash</option>
          <option value="M-Pesa">M-Pesa</option>
          <option value="Card">Card</option>
        </select>
      </div>
      <button style={{ background: '#1a2236', color: '#fff', border: 'none', borderRadius: 10, padding: '14px 28px', fontWeight: 700, fontSize: 18, cursor: 'pointer', width: '100%' }}>Place Order</button>
      <button onClick={() => setPage('cart')} style={{ marginTop: 18, background: '#e0e0e0', color: '#222', border: 'none', borderRadius: 10, padding: '10px 28px', fontWeight: 700, fontSize: 16, cursor: 'pointer', width: '100%' }}>Back to Cart</button>
    </div>
  );
}

export default App;
